import maya.cmds as cmds#order - fk chain root, ik chain root, bind chaindef bdConstrainChain(ik,fk,bind):	ikChild = cmds.listRelatives(ik, c=True, typ = 'joint')	fkChild = cmds.listRelatives(fk, c=True, typ = 'joint')	bindChild = cmds.listRelatives(bind, c=True, typ = 'joint')		cmds.parentConstraint(ik ,bind ,mo=True,weight=1)	cmds.parentConstraint(fk ,bind ,mo=True,weight=1)	if (ikChild != None) and (bindChild != None):		bdConstrainChain(ikChild,fkChild,bindChild)selection = cmds.ls(sl=True)bdConstrainChain(selection[0],selection[1],selection[2])